mapper.select:
select o.id, o.user_id, o.status, o.time,description ,o.addr_name,o.totals, o.phone,o.ssq,o.addr,l.id no,l.com from orders o left join logistics l on l.order_id = o.id where 1=1
and o.user_id = #{userId}
and o.status = #{status}
order by o.time desc limit #{start},#{pageSize}

select
o.id, o.user_id,o.status, o.time,description
,o.addr_name,o.serise,
o.phone,o.ssq,o.addr,l.id no,l.com,o.totals
from orders o
left join logistics l on l.order_id = o.id
where o.status != 1

    and o.id = #{orderId}


    and o.status = #{status}


    and o.addr_name like '%${addrName}%'


    and o.phone = #{phone}

order by o.time desc
limit #{start},#{pageSize}

select o.id, o.user_id, o.status, o.time,description ,o.addr_name,o.totals,e.name express, o.phone,o.ssq,o.addr,l.id no,l.com from orders o left join logistics l on l.order_id = o.id LEFT JOIN express e on e.com = l.com where 1=1
and o.time >= #{start}
and o.time <= #{end}
and o.status = #{status}
order by o.time desc

select
count(*)
from orders o
where o.status != 1

    and id = #{orderId}


    and status = #{status}


    and o.addr_name like '%${addrName}%'


    and o.phone = #{phone}

select count(*) countNum,status from orders where 1=1
and user_id = #{userId}
GROUP by status

mapper.update:
update orders set status= #{status} where id = #{orderId}

    and user_id = #{userId}


    and status = #{oldStatus}
